<template>
  <div>
    <div>
      <span>{{ tokenValue }}</span>
      <p>{{ device }}</p>
      <span id="name"></span>
    </div>
    <button @click="storeImg">storeImg</button>
    <button @click="showToken">showToken</button>
    <button @click="sendMessageToApp('來自web端的訊息')">
      sendMessageToApp
    </button>
    <button @click="tokenValue = ''">Clear</button>
  </div>
  <loading v-model:active="isLoading"
    :can-cancel="true"
    :is-full-page="fullPage"/>
</template>

<script>
import { onMounted, ref } from "vue";
import userDevice from "../libs/Composition/userDevice";
import Loading from 'vue-loading-overlay';

export default {
  name: 'Page',
  components: {
    Loading
  },
  setup() {
    const { device } = userDevice();

    const tokenValue = ref("");
    const isLoading = ref(false);
    const fullPage = ref(true);

    const storeImg = () => {
      // ios
      if (device === 'ios') {
        window.webkit.messageHandlers.nativeApp.postMessage({
          saveImage: {
            data: "",
          },
        });
      } 
      
      if (device === 'android') {
        // aos
        window.nativeApp.saveImage("")
      }
    };

    const showToken = () => {
      isLoading.value = true;

      if (device.value === 'ios') {
        window.webkit.messageHandlers.nativeApp.postMessage({ getAppToken: "" });
      } 

      if (device.value === 'android') {
        // TODO
        // window....
      }
    };

    const getToken = (params) => {
      tokenValue.value = params;
      isLoading.value = false;
    };

    const sendMessageToApp = (msg) => {
      window.webkit.messageHandlers.nativeApp.postMessage(msg);
    };

    onMounted(() => {
      window.getToken = getToken;
      window.saveImage = storeImg;
      showToken();
    });

    return {
      storeImg,
      showToken,
      sendMessageToApp,
      tokenValue,
      device,
      isLoading,
      fullPage
    };
  },
};
</script>

